class Solution {
    public int[] singleNumber(int[] nums) {
        /*
            MOST IMPORTANT PROPERTY FOR THIS QUESTION IS 
                XOR OF 2 SAME NUMBERS IS 0
                FOR EXAMPLE: A = 5, B = 5
                A^B = 101 ^ 101 = 0

        */
        // sample test case [1,2,1,3,2,5]
        int n = nums.length;
        int xor = 0;
        // duplicates will be eliminated and only xor of 2 unique number (lets say A and B) will be left, i.e.: A^B
        // for example test case A^B = 6
        for(int i=0;i<n;i++){
            xor ^= nums[i];
        }
        // 3 in binary is 011
        // 5 in binary is 101
        // 3^5 is 6 and in binary it is 110
        // Notice we are getting unique set bits from both numbers 
        // Now use that unique set bit and find all the numbers with that unique set bit
        int pos = 0;
        int temp = xor;
        // decrease the A^B (stored in temp) by shifting right till we find set bit
        // for sample test case pos will be 1
        while((temp&1)!=1){
            pos++;
            temp = temp>>1;
        }
        // create a mask and use & operator to find numbers with set bits at that position
        // for sample test case our mask will be 010
        int mask = 1<<pos;
        int a = 0;
        // iterate array with mask and you will get the unique number as xor of duplicate number will be 0 
        for(int i=0;i<n;i++){
            if((nums[i]&mask)>0){
                a = (nums[i]^a);
            }
        }
        // if you xor with the a you will get the second number
        int b = xor ^ a;
        int[] res = new int[2];
        res[0]=a;
        res[1]=b;
        return res;
    }
}
