class Solution {
public:
    vector<int> singleNumber(vector<int>& nums) {
        set<int> s;

        for (auto a : nums) {
            if (s.count(a)) s.erase(a);
            else s.insert(a);
        }

        return vector<int>(s.begin(), s.end());
    }
};
