class Solution {
public:
    vector<int> singleNumber(vector<int>& nums) {
        int n=nums.size();
        long long x=0;               //long long to avoid overflow

        for(auto i:nums)
            x^=i;                   // step 1 -> remove duplicates by xor all elements


        // now x will be the xor of the two single numbers
        // Observation of aXorB
        // 1) 0 in a place means both a and b had same digit in that place
        // 2) 1 in a place means a and b had different digit in that place, i.e one had 1, and other had 0 at that place

        //we can use fact 2 to seperate out the two values

        // idea -> first find a number which has 1 in the place x has

        // Eg -> let x = 0110
        // so we can pick any of 0100 and 0010

        long long sbit=x&(x-1);//gets us last set bit of x
        sbit^=x;//bit mask of last set bit of x.   e.g if x=1010 mask of x will be 0010

        // this is one of the numbers which could work of us

        // so for example let rightMostSetBit(sbit) = 0010
        // this can be used to make two partitions of array b1 and b2
        // b1 -> has 1 in the pos
        // b2 -> has 0 in the pos

        // the two numbers to find will be in seperate buckets (b) and also same numbers will be in same bucket
        // so if we just xor the values of two buckets we will get the two numbers we need

        int one=0,zero=0;
        for(auto i: nums)
        {
            if(i&sbit)
                one^=i;
            else
                zero^=i;
        }
        vector<int> v={one,zero};
        return v;
    }
};
