class Solution {
public:
    vector<int> singleNumber(vector<int>& nums) {
        long long int result = 0;
        for(const int &x : nums)
            result ^= x;
        // result = a ^ b
        // finding the first set bit from the right
        long long int mask = (result & -result);
        // find the first number which has set bit at mask position
        int a = 0;
        for(const int &x : nums) {
            if((mask & x) == mask)
                a ^= x;
        }
        // result = a ^ b
        // b = result ^ a ie. a ^ b ^ a = a ^ a ^ b = 0 ^ b = b
        int b = result ^ a;
        return {a, b};
    }
};
```

