class Solution {
    public int[] singleNumber(int[] nums) {
        int xor = 0;
        for(int num : nums) {
            xor ^= num;
        }
        int k = 0;
        while(((xor >> k) & 1) != 1) {
            k ++;
        }
        int singleNum1 = 0;
