class Solution {
    func singleNumber(_ nums: [Int]) -> [Int] {
        // get XOR of all nums
        let xorAll = nums.reduce(0, ^)

        // find any mask that can mark one different bit of the target two nums
        let mask = xorAll & (~xorAll + 1)

        // or: 
        // let mask = xorAll & -xorAll

        // traditional: 
