        //     mask = mask << 1
        // }

        // num2 is filtered by mask, so as some other pairs (pairs will not affact result)
        let num1 = nums.filter { $0 & mask != 0 }.reduce(0, ^)
        let num2 = num1 ^ xorAll

        return [num1, num2]
    }
}
