class Solution {
public:
    vector<int> singleNumber(vector<int>& nums) {
        int xorr = 0;
        for(auto it: nums){
            xorr^=it;
        }
        vector<int> ans(2,0);
        int id = -1;
        for(int i = 0 ;i < 64; i++){
            if((1<<i)&xorr){
                id = i;
                break;
            }
        }
        for(auto it: nums){
            if(it&(1<<id)){
                ans[0]^=it;
            }else{
                ans[1]^=it;
            }
        }
        return ans;
    }
};
