class Solution {
public:
    vector<int> singleNumber(vector<int>& nums) {
        vector<int> res(2);
        //xor of all value present in nums example [1,2,1,3,2,5] 1^2^3^1^2^5 = 3^5
        //Note property of xor a^a = 0 && a^0 = a
        int xorNums = 0; //this value contains xor of required nums 3^5
        for(const int &x: nums) 
            xorNums ^= x;

        //xorNums 3 binary rep 011, 5 binary rep 101 
        // 3^5 = 110 it is guranteed that in xorNums if set bit is present then 
        // at that position firstNumber bit is 0 and other is 1 bcoz we are taking xor 
        // and if one num bit is 1 and other is 0 then that bit will become 1 so we           // find rightmost set bit
        int mask = 1;
        while((xorNums & mask) == 0) 
            mask <<= 1;

        res[0] = xorNums;
        res[1] = xorNums;
        //In nums array we are taking xor of those number whose rightmost bit is set
        // [1,2,1,3,2,5] binary rep 1 = 001, 2 = 010, 3 = 011, 5 = 101
        // firstNum = 3^5(xorNums)^2^2^3 which gives 5
        // secondNum = 3^5(xorNums)^1^1^5 which gives 3 
        for(const int&x: nums)  {
            if((x & mask) == 0)
                res[0] ^= x;
            else
               res[1] ^= x;
        }

        return res;
    }
};
