    int x=0;
    int y=0;

    for(int i=0;i<nums.length;i++){
    if((rmsb & nums[i])==0){
    x=x^nums[i];
    }
    else{
    y=y^nums[i];
    }
    }

    int[]ans=new int[2];
    ans[0]=y;
    ans[1]=x;
    return ans;
