        int i;
        for(i=0; i<32; i++){
            if(x & 1<<i) break;
        }
        int a = 0, b = 0;

        for(int j: nums){
            if(j & 1<<i){
                a = a ^ j;
            }
            else{
                b = b ^ j;
            }
        }

        return {a, b};
    }
