class LFUCache {
    int tick;
    int size;
    TreeSet<Node> set;
    HashMap<Integer, Node> map;

    public LFUCache(int capacity) {
        tick = 0;
        size = capacity;
        map = new HashMap<>();
        set = new TreeSet<>((a, b) -> a.freq == b.freq ? b.tick - a.tick : b.freq - a.freq);
    }

    public int get(int key) {
        if(!map.containsKey(key)) return -1;
        Node node = map.get(key);
        addOrUpdate(node);
        map.put(key, node);
        return node.val;
    }

    public void put(int key, int value) {
        if(size == 0) return;
        Node node = map.get(key);
        if(node != null) {
            node.val = value;
            addOrUpdate(node);
        } else {
            if(map.size() == size) {
                Node last = set.pollLast();
                map.remove(last.key);
            }
            node = new Node(key, value);
            addOrUpdate(node);
        }
        map.put(key, node);
    }

    // remove node in treeSet and put it into treeSet again, update its reference in hashMap
    private void addOrUpdate(Node node) {
        set.remove(node);
        node.freq += 1;
        node.tick = ++tick;
        set.add(node);
    }

    class Node {
        int key;
        int val;
        int freq;
        int tick;
        Node prev;
        Node next;
        Node(int k, int v) {
            key = k;
            val = v;
        }
    }
}

