```
class LFUCache {
    struct Node {
        int key, val, freq;
        Node(int k,int v) : key(k),val(v),freq(1) {}
    };
    unordered_map<int,list<Node>::iterator> cache;
    unordered_map<int,list<Node>> freqs;
    int capacity, minfreq;

    void create(int key,int value) {
        freqs[1].push_front({key,value});
        cache[key] = freqs[1].begin();
        minfreq = 1;
    }
    void evict() {
        auto& list = freqs[minfreq];
        auto  node = list.back();
        cache.erase(node.key);
        list.pop_back();
        if (list.empty()) {
            freqs.erase(node.freq);
            minfreq += 1; // i'm guessing this does not really work!!!
        }
    }
    void bump(list<Node>::iterator it) {
        auto node = *it;
        freqs[node.freq].erase(it);
        if (freqs[node.freq].empty()) {
            freqs.erase(node.freq);
            if (node.freq == minfreq)
                minfreq += 1;
        }
        node.freq++;
        freqs[node.freq].push_front(node);
        cache[node.key] = freqs[node.freq].begin();
    }
public:
    LFUCache(int capacity) {
        this->capacity = capacity;
        this->minfreq  = 1;
    }
    int get(int key) {
        auto it = cache.find(key);
        if (it != cache.end()) {
            int val = it->second->val;
            bump(it->second);
            return val;
        }
        return -1;
    }
    void put(int key, int value) {
        auto it = cache.find(key);
        if (it != cache.end()) {
            it->second->val = value;
            bump(it->second);
        }
        else if (capacity != 0) { // pathological Leetcode case :-/
            if (cache.size() == capacity) evict();
            create(key,value);
        }
    }
};
```

