class LFUCache {
    struct Node {
        int key, val, freq;
        Node *prev, *next;
        Node() : key(-1), val(-1), freq(0), prev(0), next(0) {}
        Node(int k,int v,Node *node) : key(k), val(v), freq(1), prev(node), next(node->next) {
            if (next) next->prev = this;
            node->next = this;
        }
        ~Node() { 
            if (next) next->prev = prev;
            if (prev) prev->next = next;            
        }
        Node* link(Node* node) {
            if (node && node != this) {
                if (next) next->prev = prev;
                if (prev) prev->next = next;            
                next = node->next;
                node->next = this;
                if (next) next->prev = this;
                prev = node;
            }
            return this;
        }
    };
    unordered_map<int,Node*> cache;
    unordered_map<int,Node*> freqs;
    Node *head;
    int   capacity;

    void create(int key,int value) {
        cache[key] = freqs[1] = new Node(key,value,(freqs.count(1) ? freqs[1] : head));
    }
    void evict() {
        Node *node=head->next;
        if (node) {
            if (freqs[node->freq] == node)
                freqs.erase(node->freq); // we are the only node
            cache.erase(node->key);
            delete node;
        }
    }
    void bump(Node *node) {
        Node *prev=node->prev;
        int freq = ++(node->freq);

        freqs[freq] = node->link(freqs.count(freq) ? freqs[freq] : freqs[freq-1]);

        if (node == freqs[freq-1]) {
            if (prev->freq == freq-1)
                freqs[prev->freq] = prev;
            else
                freqs.erase(freq-1);
        }
    }
public:
    LFUCache(int capacity) : head(new Node()) { this->capacity = capacity; }

    int get(int key) {
        auto it = cache.find(key);
        if (it == cache.end()) return -1;
        bump(it->second);
        return it->second->val;
    }
    void put(int key, int value) {
        auto it = cache.find(key);
        if (it != cache.end()) {
            it->second->val = value;
            bump(it->second);
        }
        else if (capacity) {
            if (cache.size() == capacity) evict();
            create(key,value);
