class LFUCache {
    int CAP;
    HashMap<Integer, Integer> vals  = new HashMap();   // key -> value 
    HashMap<Integer, Integer> freqs = new HashMap();   // key -> freq
    // freq -> a list of keys with the same freq, sorted in access order. TreeMap entries are sorted by freq, first entry is the leastFreq entry
    TreeMap<Integer, LinkedHashSet<Integer>> freqToKeys = new TreeMap(); 
    public LFUCache(int capacity) {
        CAP = capacity;
    }

    public int get(int key) {
        if (vals.containsKey(key)) {
            // update freqs
            int curFreq = freqs.get(key);
            freqs.put(key, curFreq + 1);
            // update freqToKeys
            freqToKeys.get(curFreq).remove(key);
            if (freqToKeys.get(curFreq).size() == 0) freqToKeys.remove(curFreq);
            freqToKeys.computeIfAbsent(curFreq + 1, k -> new LinkedHashSet<Integer>()).add(key);
            return vals.get(key);
        } else {
            return -1;
        }
    }

    public void put(int key, int value) {
        if (CAP <= 0) return;
        // remove first if CAP exceeds (remove first before adding the new entry)
        if (vals.size() == CAP && !vals.containsKey(key)) {
            Map.Entry<Integer, LinkedHashSet<Integer>> leastFreqEntry = freqToKeys.pollFirstEntry();
            Integer evictKey = leastFreqEntry.getValue().iterator().next(); // first key
            vals.remove(evictKey);
            freqs.remove(evictKey);
            leastFreqEntry.getValue().remove(evictKey);
            if (!leastFreqEntry.getValue().isEmpty()) freqToKeys.put(leastFreqEntry.getKey(), leastFreqEntry.getValue());
        }
        // add the entry
        vals.put(key, value);
        if (freqs.containsKey(key)) get(key);
        else {
            freqs.put(key, 1);
            freqToKeys.computeIfAbsent(1, k -> new LinkedHashSet<Integer>()).add(key);
        }
    }
}
