                int removedKey = tail.next.removeLru();
                lfuMap.remove(removedKey);
                if (tail.next.isEmpty()) {
                    remove(tail.next);
                }
            }
            addKeyValueToNextNode(tail, new KeyValue(key, value));
        }
    }
