class LFUCache {
    private Map<Integer, Integer> keyToValueMap;
    private Map<Integer, Integer> keyToCountMap;
    private Map<Integer, LinkedHashSet<Integer>> countToKeysMap;
    private int capacity;
    private int minCount;

    public LFUCache(int capacity) {
        this.keyToValueMap = new HashMap<>();
        this.keyToCountMap = new HashMap<>();
        this.countToKeysMap = new HashMap<>();
        this.countToKeysMap.put(1, new LinkedHashSet<>());
        this.capacity = capacity;
        this.minCount = 0;
    }

    public int get(int key) {
        if (!keyToValueMap.containsKey(key)) return -1;
        int count = keyToCountMap.get(key);
        keyToCountMap.put(key, count + 1);
        countToKeysMap.get(count).remove(key);
        if (!countToKeysMap.containsKey(count + 1)) countToKeysMap.put(count + 1, new LinkedHashSet<>());
        countToKeysMap.get(count + 1).add(key);
        if (count == minCount && countToKeysMap.get(count).isEmpty()) minCount++;
        return keyToValueMap.get(key);
    }

    public void put(int key, int value) {
        if (capacity == 0) return;
        if (keyToValueMap.containsKey(key)) {
            keyToValueMap.put(key, value);
            get(key);
            return;
        }
        if (keyToValueMap.size() == capacity) {
            int keyToRemove = countToKeysMap.get(minCount).iterator().next();
            keyToValueMap.remove(keyToRemove);
            keyToCountMap.remove(keyToRemove);
            countToKeysMap.get(minCount).remove(keyToRemove);
        }
        minCount = 1;
        keyToValueMap.put(key, value);
        keyToCountMap.put(key, 1);
        countToKeysMap.get(1).add(key);
    }
}
