        if (maxSize <= 0)
            return;

        if (cache.count(key))
        {
            update(key);
            ::get<0>(cache[key]) = value;

            return;
        }

        if (cache.size() == maxSize)
        {
            cache.erase(freq[minFreq].back());
            freq[minFreq].pop_back();
        }

        minFreq = 1;
        freq[minFreq].push_front(key);

        cache[key] = make_tuple( value, minFreq, freq[minFreq].begin() ); 
    }
};

