class LFUCache {

    int capacity=0;
    HashMap<Integer,Integer> cache = new HashMap(); // cache
    HashMap<Integer,Integer> keyCounts= new HashMap(); // key count
    HashMap<Integer,LinkedHashSet<Integer>> freqMap= new HashMap(); // freq map
    int min=-1; //least used count
    public LFUCache(int capacity) {
        this.capacity=capacity;
        freqMap.put(1, new LinkedHashSet());
    }

    public int get(int key) {
        //if cahce does not contain key return -1
        if(!cache.containsKey(key)) return -1;
        //update the key count
        int count = keyCounts.get(key);
        keyCounts.put(key,count+1);
        //remove key from freq map
        freqMap.get(count).remove(key);
        //when the frqmap for the min count is empty then increment the min 
        if(count==min && freqMap.get(count).size()==0) min++;
        //if the new count in freq map does not exists create a new entry and add the key to the freq map
        if(!freqMap.containsKey(count+1)) freqMap.put(count+1, new LinkedHashSet());
        freqMap.get(count+1).add(key);
        return cache.get(key);


    }

    public void put(int key, int value) {
        if(capacity<=0) return;
        //if cache contains the key return value and call get so all the updates happen
        if(cache.containsKey(key)){
            cache.put(key,value);
            get(key);
            return;
        }

        //if cache size greater than capacity then remoe the least used element. This will be cominf from the minimum freqMap . remove the key from all places
        if(cache.size() >=capacity){
            int evict =freqMap.get(min).iterator().next();
            cache.remove(evict);
            freqMap.get(min).remove(evict);
            keyCounts.remove(evict);
        }
        //add new entry and reset min to 1 
        cache.put(key,value);
        keyCounts.put(key,1);
        min=1;
        freqMap.get(1).add(key);

    }
}

