class LFUCache {

public:
    #define pii pair<int,int>
    #define v first
    #define freq second


    unordered_map<int,list<int>> lfuCache; //map to get the list of elements using their freq
    unordered_map<int,pii> getParams;       // map to get the value and freq using key
    unordered_map<int,list<int>:: iterator> getIt;  // map to get the the iterator using key
    int size;
    int capacity;
    int minFreq;
    LFUCache(int capacity) {
        this->capacity=capacity;
        size=0;
        minFreq=INT_MAX;
    }
    int get(int key) {
        if(capacity!=0 && getParams.find(key)!=getParams.end()){
            int value=getParams[key].v;
            int f=getParams[key].freq;
            auto it=getIt[key];
            lfuCache[f].erase(it);     //erase this element from its current_freq to store it on current_freq+1 
            if(lfuCache[f].size()==0 && minFreq==f){
                minFreq=f+1;          //keep track if  no elements with minFreq are left
            }

            getParams[key].freq=f+1;
            lfuCache[f+1].push_front(key);
            it=lfuCache[f+1].begin();
            getIt[key]=it;
            return value;
        }else
