    ListNode tail; //like in LRU
    Map<Integer, Integer>map; //contains actual keys and values
    Map<Integer, Integer>elemToFreq; // key -> "frequency for this key"
    Map<Integer, ListNode>nodes; // "frequency from elemToFreq" -> ListNode
    int capacity;

    public LFUCache(int capacity) {
        nodes = new HashMap<>();
        elemToFreq = new HashMap<>();
        map = new HashMap<>();
        head = new ListNode(0);
        tail = new ListNode(0);
        head.next = tail;
        tail.prev = head;
        this.capacity = capacity;
    }

    public int get(int key) {
        if (map.get(key)!=null){
            incrFreq(key); //increment frequency for this key, update value in our maps and list of nodes
            return map.get(key);
        }
        return -1;
    }

    public void put(int key, int value) {
        if (capacity>0){
        //find and remove least frequent key from all maps and 
		//list of nodes (if only this key has definite frequency)
		if (map.size()==capacity && map.get(key)==null) removeLF();
		//look at it as if it's a call of get() but with 'map' update
        if (map.containsKey(key)){
            map.put(key, value);
            incrFreq(key);
        } else {
            map.put(key,value);
			//adding key and frequency '1' to our maps and list of nodes
            addNew(key);
        }
        }
    }

    public void incrFreq(int key){
		//find frequency and listnode for this key
        int freq = elemToFreq.get(key);
        ListNode old = nodes.get(freq);
		//remove key from listnode
        old.keys.remove(key);
		//find new frequency for this key (always incremented)
        int newFreq = freq+1;

		//update maps and list of nodes with new frequency
        elemToFreq.put(key, newFreq);

        if (nodes.get(newFreq)!=null){
            nodes.get(newFreq).keys.add(key);
        } else {
            ListNode node = new ListNode(newFreq);
            node.keys.add(key);
            node.next = old.next;
            node.prev = old;
            old.next.prev = node;
            old.next = node;
            nodes.put(newFreq, node);
        }
		//don't forget to delete listnode with previous frequency if only this key had such frequency
        if (old.keys.isEmpty()){
            nodes.remove(freq);
            old.next.prev = old.prev;
            old.prev.next = old.next;
        }


    }

    public void addNew(int key){
        elemToFreq.put(key,1);
		//add key to existing listnode with frequency=1 or create new listnode
        if (nodes.get(1)==null){
            ListNode node = new ListNode(1);
            node.keys.add(key);
            head.next.prev = node;
            node.next = head.next;
            head.next = node;
            node.prev = head;
            nodes.put(1, node);
        }
        else {
            nodes.get(1).keys.add(key);
        }
    }

    public void removeLF(){
	//find the first listnode => it will contain the least frequently used key
        ListNode lf = head.next;
        Iterator<Integer> it =  lf.keys.iterator();
        int toDelete = it.next();
        lf.keys.remove(toDelete);
        map.remove(toDelete);
        elemToFreq.remove(toDelete);
        if (head.next.keys.size()==0){
            nodes.remove(lf.freq);
            head.next = lf.next;
            lf.next.prev = head;
        }
    }
}

