class ListNode {
    let key: Int
    var value: Int
    var freq: Int = 1
    var prev: ListNode? = nil
    var next: ListNode? = nil

    init(_ key: Int, _ value: Int) {
        self.key = key
        self.value = value
    }
}

class FreqList {
    // "dummy" stay between the tail and the head: ....tail -> dummy -> head.....
    // head is the LRU, tail is the most recently used
    let dummy: ListNode
    var size = 0

    init() {
        dummy = ListNode(-1, -1)
        dummy.prev = dummy
        dummy.next = dummy
    }

    func append(_ node: ListNode) { // add to the tail (most recently used)
        node.prev = dummy.prev
        node.prev!.next = node
        node.next = dummy
        dummy.prev = node

        size += 1
    }

    func remove(_ node: ListNode) {
        node.next!.prev = node.prev
        node.prev!.next = node.next

        size -= 1
    }

    func popLast() -> ListNode { // remove the head (LRU) and return it
        let head = dummy.next!
        remove(head)
        return head
    }
}

class LFUCache {
    var keyToNode = [Int: ListNode]()
    var freqToList = [Int: FreqList]()
    var size = 0
    var minFreq = 0
    let capacity: Int

    init(_ capacity: Int) {
        self.capacity = capacity
    }

    // get: return keyToNode[key], update node
    func get(_ key: Int) -> Int {
        if let node = keyToNode[key] {
            update(node)
            return node.value
        } else {
            return -1
        }
    }

    // put: if there exist "node", update it
    // else, check capacity, if full, remove head (LRU)
    // then, add the new node
    func put(_ key: Int, _ value: Int) {
        if capacity == 0 { return }

        if let node = keyToNode[key] {
            node.value = value
            update(node)
        } else {
            if size == capacity { // full
                let lruNode = freqToList[minFreq]!.popLast()
                keyToNode[lruNode.key] = nil
                size -= 1
            } 

            let newNode = ListNode(key, value)
            keyToNode[key] = newNode

            if freqToList[1] == nil {
                freqToList[1] = FreqList()
            }
            freqToList[1]!.append(newNode)

            size += 1
            minFreq = 1
        }
    }


    // update: increase freq, remove node from oldFreq's list, add it to new list
    // if oldFreq's list is empty, minReq is now this newFreq
    func update(_ node: ListNode) {
        let oldFreq = node.freq
        let newFreq = oldFreq + 1

        freqToList[oldFreq]!.remove(node)
        if minFreq == oldFreq && freqToList[oldFreq]!.size == 0 {
            minFreq = newFreq
        }

        node.freq = newFreq

        if freqToList[newFreq] == nil {
            freqToList[newFreq] = FreqList()
        }
        freqToList[newFreq]!.append(node)
    }
}
