                min++;
            }
            putCount(key, count + 1);
            return;
        }
        if (keyToVal.size() >= capacity) {
            evict(countToLRUKeys.get(min).iterator().next());
        }
        min = 1;
        putCount(key, 1);
        keyToVal.put(key, value);
    }

