class LFUCache {
public:
    int capacity, lfu = 0;

    unordered_map<int, pair<int, int>>          key_val_freq;   // key to {value,freq}
    unordered_map<int, list<int>>               freq_key;       // freq to key list 
    unordered_map<int, list<int>::iterator>     key_freq_iter;  // key to list iterator;

    LFUCache(int capacity) : capacity(capacity) {
    }

    int get(int key) {
        if (!key_val_freq.count(key)) {
            return -1;
        }
        updateLFU(key);
        return key_val_freq[key].first;
    }

    void put(int key, int value) {
        if (!capacity) {
            return;
        }

        if (key_val_freq.size() == capacity && key_val_freq.count(key) == 0)
            evict();

        if (key_val_freq.count(key)) {
            key_val_freq[key].first = value;
            updateLFU(key);
        } else {
            key_val_freq[key] = {value, 1};
            freq_key[1].push_back(key);

            key_freq_iter[key] = --freq_key[1].end();  // store the position of the key in freq_key list !!!    
            lfu = 1;
        }
    }


    void updateLFU(int key) {        
        int freq = key_val_freq[key].second;

        key_val_freq[key].second++;             // update freq for the key
        auto iter = key_freq_iter[key];

        freq_key[freq].erase(iter);
        freq_key[freq + 1].push_back(key);

        key_freq_iter[key] = --freq_key[freq + 1].end();

        if (freq_key[lfu].empty()) {
            lfu++;
        }
    }

    void evict() {
        int key = freq_key[lfu].front();
        freq_key[lfu].pop_front();

        key_val_freq.erase(key);
        key_freq_iter.erase(key);
    }

};
