import java.util.HashMap;


public class LFUCache {
	HashMap<Integer, Node> valMap;
	HashMap<Integer, LinkedList> freqMap;
	int minFreq;
	int capacity;


	public LFUCache(int capacity) {
		this.valMap = new HashMap<>();
		this.freqMap = new HashMap<>();
		this.minFreq = 0;
		this.capacity = capacity;
	}

	public int get(int key) {
		Node curr = valMap.get(key);
		if(curr == null) {
			return -1;
		}
		updateNode(curr);
		return curr.val;
	}

	public void put(int key, int val) {
		if(capacity == 0) return;

		if(valMap.containsKey(key)) {
			valMap.get(key).val = val;
			updateNode(valMap.get(key));

		}else {
			if(valMap.size() == capacity) {
				LinkedList list = freqMap.get(minFreq);
				valMap.remove(list.tail.prev.key);
				list.removeNode(list.tail.prev);
			}
			minFreq = 1;
			Node node = new Node(key, val);
			LinkedList list = freqMap.getOrDefault(1, new LinkedList());
			list.addNode(node);
			freqMap.put(1, list);
			valMap.put(key, node);
		}
	}

	private void updateNode(Node n) {
		LinkedList list = freqMap.get(n.freq);
		list.removeNode(n);
		if(n.freq  == minFreq && list.size == 0) {
			minFreq++;
		}
		n.freq++;
		LinkedList newList = freqMap.getOrDefault(n.freq, new LinkedList());
		newList.addNode(n);
		freqMap.put(n.freq, newList);
	}


	private class Node{
		Node next;
		Node prev;
		int key;
		int val;
		int freq;

		Node(){}
		Node(int key, int val){
			this.key = key;
			this.val = val;
			this.next = null;
			this.prev = null;
			this.freq = 1;
		}
	}

	private class LinkedList{
		Node head;
		Node tail;
		int size;
		public LinkedList() {
			head = new Node();
			tail = new Node();
			head.next = tail;
			tail.prev = head;
			size = 0;
		}

		public void addNode(Node n) {
			n.next = head.next;
			head.next.prev = n;
			head.next = n;
			n.prev = head;
			size++;
		}

		public void removeNode(Node n) {
			n.prev.next  = n.next;
			n.next.prev = n.prev;
			size--;
		}
	}

}

