from sortedcontainers import SortedList
class Node:
    def __init__(self,key,val,freq=1,time=0):
        self.key=key
        self.val=val
        self.freq=freq
        self.time=time
class LFUCache:

    def __init__(self, capacity: int):
        self.heap=SortedList(key=lambda x:(-x.freq,-x.time))
        self.node_map={}
        self.cap=capacity
        self.time=0

    def add(self,node):
        self.heap.add(node)
        self.node_map[node.key]=node

    def remove(self,node):
        self.node_map.pop(node.key)
        self.heap.remove(node)

    def get(self, key: int) -> int:
        if key not in self.node_map:
            return -1

        else:
            node=self.node_map[key]
            self.remove(node)
            node.freq+=1
            node.time=self.time
            self.add(node)
            self.time+=1
            return node.val

    def put(self, key: int, value: int) -> None:
        old_freq=0
        if self.cap==0:
            return
        if key not in self.node_map and len(self.node_map)>=self.cap:
            pop_node=self.heap[-1]
            self.remove(pop_node)
        if key in self.node_map:
            old_node=self.node_map[key]
            old_freq=old_node.freq
            self.remove(old_node)
        node=Node(key,value,1+old_freq,self.time)
        self.time+=1
        self.add(node)
