class LFUCache {
    //key - val map
    Map<Integer, Integer> kvMap = new HashMap<>();
	//key - freq map
    Map<Integer, Integer> kfMap = new HashMap<>();
	//freq - keyList map
    Map<Integer, List<Integer>> fkMap = new HashMap<>();
    int min = 1;
    int cap;

    public LFUCache(int capacity) {
        cap = capacity;
    }

    public int get(int key) {
        if(!kvMap.containsKey(key)) return -1;
        //update freq
        updateFreq(key);
        return kvMap.get(key);
    }
    public void updateFreq(int key){
        int count = kfMap.get(key);
        kfMap.put(key, count + 1);//increasing the count by 1

        //remove the key from the key list if origin frequency 
        fkMap.get(count).remove(Integer.valueOf(key));
		//put the key into the end of new list
        fkMap.putIfAbsent(count + 1, new ArrayList<>());
        fkMap.get(count + 1).add(key);

//if prev count is min and there's no more key in that list, means new min = count + 1
        //update min if needed
        if(min == count && fkMap.get(count).isEmpty()) 
            min = count + 1;
    }
    public void evict(){
        //find the key to delete
        int k = fkMap.get(min).get(0); //if there's multiple, the head of list is the LRU

        kvMap.remove(k);
        kfMap.remove(k);
        fkMap.get(min).remove(0);
    }

    public void put(int key, int val) {
        if(!kvMap.containsKey(key)){
            kvMap.put(key, val);
            kfMap.put(key, 1);
            fkMap.putIfAbsent(1, new ArrayList<>());
            fkMap.get(1).add(key);
			//do evict before updating min
            while(kvMap.size() > cap){
                evict();
            }
            min = 1;
        }else{
            kvMap.put(key, val);
            updateFreq(key);
        }
    }
}
