class DLLNode {
    constructor(key, val, freq) {
        this.key = key;
        this.val = val;
        this.freq = freq;
        this.next = null;
        this.prev = null;
    }
}

class DLL {
    constructor() {
        this.head = null;
        this.tail = null;
    }
    addNode(node) {
        if(!this.head) {
            this.head = node;
            this.tail = node;
        } else {
            this.tail.next = node;
            node.prev = this.tail;
            this.tail = this.tail.next;
        }
    }
    removeNode(node) {
        if(node === this.head && this.head.next) {
            this.head = this.head.next;
            this.head.prev = null;
        } else if(node === this.head) {
            this.head = null;
            this.tail = null;
        } else if(node === this.tail) {
            this.tail = this.tail.prev;
            this.tail.next = null;
        } else {
            if(node.prev) {
                node.prev.next = node.next;    
            }
            if(node.next) {
                node.next.prev = node.prev;    
            }
        }
        node.next = null;
        node.prev = null;
    }
    isEmpty() {
        return this.head === null;
    }
}

class LFUCache {
    constructor(capacity) { // O(1)
        this.size = capacity;    // total size
        this.capacity = capacity;    // current size
        this.keyMap = new Map();    // key to node mapping
        this.freqMap = new Map();  // freq to DLL of nodes mapping
        this.minFreq = 1;         // keeps track of the min freq of a key in the cache
    }
    get = function(key) {    // O(1)
		// if size is 0 or key is not present in the cache, return -1
        if(!this.size || !this.keyMap.has(key)) {
            return -1;
        }
        const keyNode = this.keyMap.get(key);
        const newFreq = keyNode.freq + 1;

        // remove node from curr-freq mapped DLL
        this.freqMap.get(keyNode.freq).removeNode(keyNode);
        if(this.freqMap.get(keyNode.freq).isEmpty()) {
            this.freqMap.delete(keyNode.freq);
            if(this.minFreq === keyNode.freq) {    // update the min-freq if there are no keys with that freq
                this.minFreq = newFreq;
            }
        }
        keyNode.freq = newFreq;    // update the freq of the key

        // add node to the new freq mapped DLL
        const vals = this.freqMap.has(newFreq) ? this.freqMap.get(newFreq) : new DLL();
        vals.addNode(keyNode);
        this.freqMap.set(newFreq, vals);
        return keyNode.val;
    }
    put = function(key, value) {
        if(!this.size) {     // if the cache size is 0, return
            return;
        }
        if(this.keyMap.has(key)) {    // cache already has key, need to update the value
            this.keyMap.get(key).val = value;
            this.get(key);    // this will handle updating the freq and the maps
            return;
        } else if(this.capacity === 0) {    // if the cache is full, need to evict the least frequently used key
            this.evictLFU();
        }
        const node = new DLLNode(key, value, 1);    // create a new node and add to the maps
        if(!this.freqMap.has(1)) {
            this.freqMap.set(1, new DLL());
        }
        this.keyMap.set(key, node);
        this.freqMap.get(1).addNode(node);
        this.minFreq = 1;
        --this.capacity;
    }
    evictLFU = function() {
		// remove the nodes from the head and add to the tail - DLL
		// the first node with the min-freq will be least recently used
        const list = this.freqMap.get(this.minFreq);
        const node = list.head;

		// updating the maps
        this.keyMap.delete(node.key);
        list.removeNode(node);        
        if(list.isEmpty()) {
            this.freqMap.delete(this.minFreq);
        }
