		public LFUCache(int capacity) {
			this.capacity = capacity;
			countMap = new HashMap<Integer, Integer>();
			map = new LinkedHashMap<Integer, Integer>(capacity);
		}

		private int getKeyToRemove(){
			int lFUKey = Integer.MIN_VALUE;
			int minValue = Integer.MAX_VALUE;
			set = new HashSet<Integer>();

			for(int key : countMap.keySet()){
				if(countMap.get(key) < minValue){
					minValue = countMap.get(key);
					lFUKey = key;
				}   
			}

			for(int key : countMap.keySet()){ //check for equal Frequency.
				if(countMap.get(key) == countMap.get(lFUKey)){
					set.add(key);
				}
			}

			return lFUKey;
		}


		public int get(int key) {
			int value = -1;
			if(map.containsKey(key)){
				value = map.get(key);
				map.remove(key);
				map.put(key, value);
				countMap.put(key, countMap.get(key) + 1);
			}

			return value;    
		}

		public void put(int key, int value) {
			if(map.size() == capacity && !map.containsKey(key)){
				int keyToRemove = getKeyToRemove();
