        self.prev = prev

class DLinkedList:
    def __init__(self):
        self.head = Node()
        self.tail = Node()
        self.head.next = self.tail
        self.tail.prev = self.head
        self._size = 0

    @property
    def size(self):
        return self._size

    def insert(self, node):
        node.next = self.tail
        node.prev = self.tail.prev
        self.tail.prev.next = node
        self.tail.prev = node
        self._size += 1

    def pop(self, node=None):
        if not node:
            node = self.head.next
        prev = node.prev
        next = node.next
        prev.next = next
        next.prev = prev
        self._size -= 1
        return node

    def move_to_tail(self, node):
        prev = node.prev
        next = node.next
        prev.next = next
        next.prev = prev
        self.insert(node)


class LFUCache:
    def __init__(self, capacity: int):
        self._min_freq = 0
        self.cache = {}
        self.capacity = capacity
        self.freq = defaultdict(DLinkedList)

    def _update(self, node):
        f = node.freq
        self.freq[f].pop(node)
        if f == self._min_freq and self.freq[f].size == 0:
            self._min_freq = f+1
        node.freq += 1
        self.freq[node.freq].insert(node)

    def get(self, key: int) -> int:
        if key in self.cache:
            node = self.cache[key]
            self._update(node)
            return node.val
        else:
            return -1

    def put(self, key: int, value: int) -> None:
        if self.capacity == 0:
            return;

        if key in self.cache:
            node = self.cache[key]
            self._update(node)
            node.val = value
        else:
            if len(self.cache) >= self.capacity:
                node = self.freq[self._min_freq].pop()
                del self.cache[node.key]
            node = Node(key=key, val=value)
            self.cache[key] = node
            self._min_freq = 1
            self.freq[1].insert(node)    


