type LFUCache struct {
	cap     int
	min     int
	cache   map[int]*list.Element
	buckets map[int]*list.List
}

type LFUCacheElement struct {
	Key         int
	Val         int
	Frequency   int
}

func Constructor(capacity int) LFUCache {
	buckets := make(map[int]*list.List)
	buckets[1] = list.New()
	newCache := LFUCache{
		cap: capacity,
		min: 1,
		cache: make(map[int]*list.Element),
		buckets: buckets,
	}
	return newCache
}

func (lfc *LFUCache) Get(key int) int {
	e, ok := lfc.cache[key]
	if ok {
		lfuCacheElement := e.Value.(*LFUCacheElement)
		oldBucket := lfc.buckets[lfuCacheElement.Frequency]
		oldBucket.Remove(e)
		if lfuCacheElement.Frequency == lfc.min && oldBucket.Len() == 0 {
			lfc.min++
		}
		lfuCacheElement.Frequency++
		newBucket, ok := lfc.buckets[lfuCacheElement.Frequency]
		if !ok {
			newBucket = list.New()
			lfc.buckets[lfuCacheElement.Frequency] = newBucket
		}
        lfc.cache[key] = newBucket.PushFront(lfuCacheElement)
        return lfuCacheElement.Val
	} else {
		return -1
	}
}

func (lfc *LFUCache) Put(key int, value int)  {
	e, ok := lfc.cache[key]
	if ok {
		lfuCacheElement := e.Value.(*LFUCacheElement)
		lfuCacheElement.Val = value
		lfc.Get(key)
	} else {
        newLfuCacheElement := LFUCacheElement{Key: key, Val: value, Frequency: 1}
        newElement := lfc.buckets[1].PushFront(&newLfuCacheElement)
		lfc.cache[key] = newElement
        if len(lfc.cache) > lfc.cap {
			lfBucket := lfc.buckets[lfc.min]
			lrElement := lfBucket.Back()
			lfBucket.Remove(lrElement)
			lfuCacheElement := lrElement.Value.(*LFUCacheElement)
			delete(lfc.cache, lfuCacheElement.Key)
		}
        lfc.min = 1
	}
}
