        self.val = val
        self.prev= prev
        self.next= next
class LinkedList: # rhis implements logic of LFU cache
# solve LRU before this.
    def __init__(self):
        self.left= ListNode(0)        
        self.right= ListNode(0)
        self.left.next =self.right
        self.map={}
    def length(self):
        return len(self.map)
    def pushRight(self,val):
        node= ListNode(val,self.right.prev,  self.right)
        self.map[val]=node
        self.right.prev=node
        node.prev.next=node

    def get(self, key: int) -> int:
        return len(self.map)        
    def popLeft(self): # popping the LRU element
        res = self.left.next.val
        self.pop(self.left.next.val)
        return res
    def update(self,val):
        self.pop(val)
        self.pushRight(val) #push to the first one 


    def put(self, key: int, value: int) -> None:
        node = LisNode(val, self.right.prev, self.right)
        self.map[val]=node # save a reference of that node in the hashmap
        self.right.prev=node
        node.prev.next= node

    def pop(self, val):
        if val in self.map:
            node= self.map[val]
            next, prev= node.next, node.prev
            next.prev=  prev
            prev.next = next

class LFUCache:
    def __init__(self, capacity:int):
        self.cap= capacity
        self.lfuCnt=0
        self.valMap={}
        self.countMap = collections.defaultdict(int)
        self.listMap = collections.defaultdict(LinkedList)

    def counter(self ,key):
        cnt= self.countMap[key]
        self.countMap[key]  += 1
        self.listMap[cnt].pop(key) # follows the code that written above.
        self.listMap[cnt+1].pop(key)

        if cnt== self.lfuCnt and self.listMap[cnt].length() ==0:
            self.lfuCnt +=1

    def get(self,key:int)->int:
        if key not in self.valMap:
            return -1
        self.counter(key)
        return self.valMap[key]

    def put(self,key:int, value:int) -> None:
        if self.cap ==0:
            return 

        if key not in self.valMap and len(self.valMap) == self.cap:
            # if capacity is full so now delte the value
            res = self.listMap[self.lfuCnt].popLeft() # deleting leasst recently used.
            self.valMap.pop(res)
            self.countMap.pop(res)
        self.valMap[key] = value
        self.counter(key)
        self.lfuCnt = min(self.lfuCnt, self.countMap[key]) 



