```swift
class Solution {
    func minPathSum(_ grid: [[Int]]) -> Int {
        var dp = grid
        let rs = grid.count, cs = grid[0].count
        (1..<cs).forEach {
            dp[0][$0] += dp[0][$0-1]
        }
        (1..<rs).forEach {
            dp[$0][0] += dp[$0-1][0]
            for i in 1..<cs {
                dp[$0][i] += min(dp[$0-1][i], dp[$0][i-1])
            }
        }
        return dp[rs-1][cs-1]
    }
}
