class Solution {
public:
**The idea is to traverse from right to left (starting from bottom to top).
Each cell of the dp will contain cost to reach the end cell.**


    int minPathSum(vector<vector<int>>& grid) {
        int row=grid.size();
        int col=grid[0].size();
        int dp[row][col];
        for(int i=row-1;i>=0;i--){
            for(int j=col-1 ; j>=0 ; j--){
                if(i==row-1 and j==col-1){ //means we are at end cell
                    dp[i][j]=grid[i][j];
                }
                else if(i==row-1){  //means we are at last row
                    dp[i][j]=grid[i][j]+dp[i][j+1];
                }
                else if(j==col-1){ //means we are at last col
                    dp[i][j]=grid[i][j]+dp[i+1][j];
                }
                else{  //rest of the cell
                    dp[i][j] = grid[i][j] + min(dp[i+1][j],dp[i][j+1]);
                }

            }
        }
        return dp[0][0];
    }
};

