int findMinSumInGrid(vector<vector<int>>& grid,int row,int col,int currRow,int currCol){

    if(currRow == row-1 && currCol == col-1){
        return grid[currRow][currCol];
    }

    if(currRow >= row || currCol >= col){
        return INT_MAX;
    }

    return grid[currRow][currCol] + min(findMinSumInGrid(grid,row,col,currRow,currCol+1),findMinSumInGrid(grid,row,col,currRow+1,currCol));

