    class Solution {
    public:
        int minPathSum(vector<vector<int>>& grid) {
            int m = grid.size();
            int n = grid[0].size();
            vector<int> dp(n);

            dp[0] = grid[0][0];
            for (int j = 1; j < n; j++) {   // init
                dp[j] = dp[j-1] + grid[0][j];
            }

            for (int i = 1; i < m; i++) {
                for (int j = 0; j < n; j++) {
                    dp[j] = (j == 0 ? dp[j] : min(dp[j], dp[j-1])) + grid[i][j];
                }   
            }
            return dp[n-1];
        }
