                if(i==0 && j!=0)
                    grid[i][j]+=grid[i][j-1];
                else if(j==0 && i!=0)
                    grid[i][j]+=grid[i-1][j];
                else if(i && j)
                    grid[i][j]+=min(grid[i][j-1], grid[i-1][j]);
