        return grid[i][j] + min(down, right); // since we want minimum we will choose minimum from down and right + value at the present cell.
    }
public:
    int minPathSum(vector<vector<int>>& grid) {
        int row = grid.size();
        int col = grid[0].size();
        return recursion(0, 0, row, col, grid); // at index (0, 0) we will have the total min cost to reach end from (0,0).
    }
};
