class Solution {
public:

    int dp[2001][2001];
    int rec(vector<vector<int> > &A,int i,int j)
    {
        int m=A.size(),n=A[0].size();

        if(i>=m or j>=n) return INT_MAX;
        if(i==m-1 and j==n-1) return A[i][j];
        if(dp[i][j]!=-1) return dp[i][j];

        return dp[i][j]=min(rec(A,i+1,j),rec(A,i,j+1))+A[i][j];
    }

    int minPathSum(vector<vector<int>>& grid) {
        memset(dp,-1,sizeof(dp));
        return rec(grid,0,0);
    }
};
