class Solution {
public:


// SOLUTION 1 : RECURSIVE APPROACH
    int solve(int row, int col, vector<vector<int>>&grid){
        // base case
        if(row == 0 && col == 0){
            return grid[0][0];
        }

        // out of bound case
        if(row < 0 || col < 0){
            return 1e9;
        }

        // recursive calls
        int up = grid[row][col] + solve(row-1, col, grid);
        int left = grid[row][col] + solve(row, col-1, grid);
        int ans = min(up , left);

        return ans;
    }

// SOLUTION 2 : RECURSIVE + MEMOIZATION APPROACH
    int solve2(int i, int j, vector<vector<int>>&grid, vector<vector<int>>&dp){
        // base case
        if(i == 0 && j == 0){
            return grid[0][0];
        }

        if( i < 0 || j < 0){
            return 1e9;
        }

        if(dp[i][j] != -1){
            return dp[i][j];
        }

        // recursive calls
        int up = grid[i][j] + solve2(i-1, j, grid, dp);
        int left = grid[i][j] + solve2(i, j-1, grid, dp);
        int ans = min(up, left);

        return dp[i][j] = ans;
    }


// SOLUTION 3 : TABULATION METHOD (BOTTOM UP APPORACH)
    int solve3(vector<vector<int>>& grid){
        int row = grid.size();
        int col = grid[0].size();

        vector<vector<int>>dp(row, vector<int>(col, 0));

        for(int i = 0; i<row; i++){
            for(int j = 0; j<col; j++){

                if(i == 0 && j == 0){
                    dp[i][j] = grid[i][j];
                }

                else{

                    int up = grid[i][j];
                    if(i > 0) up+= dp[i-1][j];
                    else up+= 1e9;

                    int left = grid[i][j];
                    if(j > 0) left+= dp[i][j-1];
                    else left += 1e9;

                    dp[i][j] = min(up, left);
                }
            }
        }

        return dp[row-1][col-1];
