// function for minPath getting the minPath string....
    string minPath(vector<vector<int>> &grid)
    {
        int m = grid.size(), n = grid[0].size();
        vector<vector<int>> dp(m, vector<int>(n, 0));
        vector<vector<string>> path(m, vector<string>(n));
        dp[0][0] = grid[0][0];

        for (int i = 1; i < m; i++)
        {
            dp[i][0] = dp[i - 1][0] + grid[i][0];
            path[i][0] += path[i - 1][0] + "D";
