        if(i==0 && j==0) return a[i][j];
        //out of boundary case
        if(i<0 || j<0) return 1e8;  

        int up = a[i][j] + func(i-1,j,a);
        int left = a[i][j] + func(i,j-1,a);

        return min(left,up);
