#define ll long long
class Solution {
public:
    int maximumGap(vector<int>& nums) {
        ll maxnum=*max_element(nums.begin(),nums.end());
        ll minnum=*min_element(nums.begin(),nums.end());
        int bsize=nums.size()-1;
        if(bsize==0 or minnum==maxnum)return 0;
        double width=(maxnum-minnum + 0.0)/bsize;
        int maxin[bsize];
        int minin[bsize];
        for(int i=0;i<bsize;i++)
        {
            maxin[i]=INT_MIN;
            minin[i]=INT_MAX;
        }
        for(int i=0;i<bsize+1;i++)
        {
            if(nums[i]==minnum or nums[i]==maxnum)
                continue;
            int bnum=(nums[i]-minnum+0.0)/width;
            maxin[bnum]=max(nums[i],maxin[bnum]);
            minin[bnum]=min(nums[i],minin[bnum]);
        }
        ll res=0;
        ll prev=minnum;
        for(int i=0;i<bsize;i++)
        {
            if(maxin[i]==INT_MIN)
                continue;
            res=max(res,minin[i]-prev);
            prev=maxin[i];
        }
        return max(res,maxnum-prev);
    }
};
