def maximumGap(self, nums: List[int]) -> int:
	n=len(nums)
	if n==1:
		return 0
	min_val = min(nums)
	max_val = max(nums)

	if max_val==min_val:
		return 0

	Range = max_val-min_val


	width = Range/n

	partition = [ [] for _ in range( int(min_val/width),int(max_val/width)+1)]

	shift = min_val/width

	for num in nums:
		temp = num/width
		partition[int(temp-shift)].append(num)

	j=0
	dif = 0
	for i in range(1,len(partition)):
		if partition[i]:
			dif = max(dif,min(partition[i])-max(partition[j]))
			j=i

	return dif

