void radix( vector<int>&A , int st , int en , int p )
{
    if( p < 0 )return ;
    if( st >= en )return ;
    int l = st ;
    int h = en ;
    while( l <= h )
    {
        if( A[l]&(1<<p) )
            swap( A[l] , A[h--] );
        else
            l++;
    }
    radix( A , st , l-1 , p-1 );
    radix( A , l , en , p-1 );
}

