class Solution {

    void radixSort(vector<int> &nums, int value, int N) {

        int countDigit[10] = {0};

        for(int i = 0; i < N; i++) {
            int element = nums[i]/value;
            countDigit[element%10] += 1;
        }

        for(int i = 1; i < 10; i++) {
            countDigit[i] += countDigit[i - 1];
        }

        int sorteByPlace[N];

        int ans[N];

        for(int i = N - 1; i >= 0; i--) {

            int element = nums[i]/value;
	        int correctIndex = countDigit[element % 10] - 1;            
            sorteByPlace[correctIndex] = nums[i];	        
	        countDigit[element % 10] -= 1;
	    }

        for(int i = 0; i < N; i++) {
            nums[i] = sorteByPlace[i];
        }
    }

public:
    int maximumGap(vector<int>& nums) {

        int N = nums.size();
        if(N < 2) return 0;

        int maxElement = *max_element(nums.begin(), nums.end());
        int value = 1;

		// Iterate till each digit from one's place to the
		// ten's to the hundred's  and so on
        while(value <= maxElement) {
            radixSort(nums, value, N);
            value *= 10;
        }

        int maxDifference = -1;
        for(int i = 0; i < N - 1; i++) {
            maxDifference = max(maxDifference, nums[i + 1] - nums[i]);
        }

        return maxDifference;

    }
};
