// Radix sort
class Solution {
    public int maximumGap(int[] nums) {

        int n = nums.length;

        for (int i = 0; i < 10; i++) { // O(N)
            int[] sorted = countSort(nums, i);
            nums = sorted;
        }
        int res = Integer.MIN_VALUE;
        for (int i = 1; i < n; i++) { // O(N)
            res = Math.max(res, nums[i] - nums[i - 1]);
        }
        if (res == Integer.MIN_VALUE) return 0;
        return res;
    }

    private int[] countSort(int[] nums, int d) {
        int n = nums.length;
        int[] sorted = new int[n];
        int[] count = new int[10];
        for (int i = 0; i < n; i++) { // o(n)
            int curr = nums[i] / (int) Math.pow(10, d) % 10;
            count[curr]++;
        }
        for (int j = 1; j < 10; j++) { // o(1)
            count[j] += count[j - 1];
        }
        for (int i = n - 1; i >= 0; i--) { // (n)
            int curr = nums[i] / (int) Math.pow(10, d) % 10;
            int index = count[curr] - 1;
            count[curr]--;
            sorted[index] = nums[i];
        }
        return sorted;
    }
}



