class Solution {
public:
    int maximumGap(vector<int>& nums) {
        const int n = nums.size();
        if(n<2) return 0;
        int maxE = *max_element(nums.begin(),nums.end());
        int minE = *min_element(nums.begin(),nums.end());
        int avgIntervals = (int)ceil(double(maxE-minE)/double(n-1));
        vector<int> maxA(n-1,INT_MIN); // n-1 buckets enough as per pegion hole principle
        vector<int> minA(n-1,INT_MAX);
        // We shouldn't worry about other elements. In each bucket, minimum and maximum is enough.
        for(int i=0; i<n-1; i++) {
            if(nums[i]==minE || nums[i]==maxE) continue;  //[1,1,1,1]. AvgIntervals=0 and divByZero next line
            int bucketIndx = (nums[i]-minE)/avgIntervals; // Magic formula to find bucket index.
            maxA[bucketIndx] = max(maxA[bucketIndx],nums[i]);
            minA[bucketIndx] = min(minA[bucketIndx],nums[i]);
        }

        //We didn't place min element and max element in the buckets before. We would start prev with min ele
        int gap = INT_MIN, prev = minE;
        for(int i=0; i<n-1; i++) {
            if(maxA[i]==INT_MIN || minA[i]==INT_MAX) continue; // Empty bucket
            gap = max(gap,minA[i]-prev); // Find diff b/w minimum of current and maximum of prev bucket
            prev = maxA[i];
        }
        //Finally compare with Maximum element
        gap=max(gap, maxE-prev);
        return gap;
    }
};
