```
func maximumGap(_ nums: [Int]) -> Int {
	if nums.isEmpty || nums.count == 1 { return 0 }
	let count = nums.count
	let maxNum = nums.max()!
	var temp = nums
	var exp = 1

	while maxNum / exp > 0 {
		var freqs = Array(repeating: 0, count: 10)

		for num in temp {
			freqs[(num / exp) % 10] += 1
		}

		for i in 1...9 {
			freqs[i] += freqs[i - 1]
		}

		for num in temp.reversed() {
			let digit = (num / exp) % 10
			freqs[digit] -= 1
			temp[freqs[digit]] = num
		}
		exp *= 10
	}

	var maxGap = 0
	for i in 1..<count {
		maxGap = max(maxGap, temp[i] - temp[i - 1])
	}

	return maxGap
}
```

