func maximumGap(_ nums: [Int]) -> Int {
	if nums.isEmpty || nums.count == 1 { return 0 }
	let count = nums.count
	let minNum = nums.min()!
	let maxNum = nums.max()!
	if minNum == maxNum { return 0 }
	let gap = Int((Double(maxNum - minNum)/Double(count - 1)).rounded(.up))

	var minBucket = Array(repeating: Int.max, count: count)
	var maxBucket = Array(repeating: Int.min, count: count)

	for num in nums {
		let index = (num - minNum) / gap

		minBucket[index] = min(minBucket[index], num)
		maxBucket[index] = max(maxBucket[index], num)
	}

	var maxGap = 0
	var prev = maxBucket[0]
	for i in 1..<count {
		if minBucket[i] == Int.max { continue }
		maxGap = max(maxGap, minBucket[i] - prev)
		prev = maxBucket[i]
	}

	return maxGap
}
